$(function(){$("#pop-login,#pop-register,#pop-forgotpass").on("hidden.bs.modal",
		function(){
					$(this).find("form").trigger("reset");
					$(this).find("#msg-login,#msg-register,#msg-forgotpass").html("");
					$(this).find("span.text-danger").html("")});
					var n=$("#btnLogin"),t=$("#btnRegister"),i=$("#btnForgotPass"),r=n.html(),u=t.html();
					$("#frmLogin").submit(function(){return $(this).valid()&&$.ajax({url:this.action,type:this.method,data:$(this).serialize(),beforeSend:function(){loading(n)},success:function(t){t.success?location=location:(endLoading(n,r),$("input[name=Password]").val(""),$("#msg-login").html('<div class="alert alert-danger">'+t.message+"<\/div>"))},error:function(n){alert(n.statusText)}}),!1});$("#frmRegister").submit(function(){return $(this).valid()&&$.ajax({url:this.action,type:this.method,data:$(this).serialize(),beforeSend:function(){loading(t)},success:function(n){n.success?($("#frmRegister").trigger("reset"),$("#msg-register").html('<div class="alert alert-success">'+n.message+"<\/div>")):($("input[name=Password]").val(""),$("#msg-register").html('<div class="alert alert-danger">'+n.message+"<\/div>"))},error:function(n){alert(n.statusText)},complete:function(){endLoading(t,u)}}),!1});
					$("#frmForgotPass").submit(function(){return $(this).valid()&&$.ajax({url:this.action,type:this.method,data:$(this).serialize(),beforeSend:function(){loading(i)},success:function(n){n.success?($("#frmForgotPass").trigger("reset"),$("#msg-forgotpass").html('<div class="alert alert-success">'+n.message+"<\/div>")):$("#msg-forgotpass").html('<div class="alert alert-danger">'+n.message+"<\/div>")},error:function(n){alert(n.statusText)},complete:function(){endLoading(i,i.text())}}),!1})})